String html2String(int x, int y){
  return '<div id="map" style="width:500px;height:400px;"></div><script type="text/javascript" src="//dapi.kakao.com/v2/maps/sdk.js?appkey=73c3230e3a9a1f0f4a66dff04da3abcd"> var container = document.getElementById("map");var options = {center: new kakao.maps.LatLng(33.450701, 126.570667),level: 3};var map = new kakao.maps.Map(container, options); </script>';
}

String ht(double x, double y){
  return '<!DOCTYPE html><html><head> <meta charset="utf-8"><title>지도 이동시키기</title></head><body> <div id="map" style="width:100%;height:180px;"></div><script type="text/javascript" src="//dapi.kakao.com/v2/maps/sdk.js?appkey=73c3230e3a9a1f0f4a66dff04da3abcd"></script><script> var markerPosition  = new kakao.maps.LatLng($x, $y);var marker = new kakao.maps.Marker({position: markerPosition});var mapContainer = document.getElementById("map"), mapOption = {center: new kakao.maps.LatLng($x, $y),level: 3};var map = new kakao.maps.Map(mapContainer, mapOption); marker.setMap(map); </script></body></html>';
}

